import ListItem from "./ListItem"
import dataRecipes from "../data/data.json"
import { useState } from "react"

const List = () => {

    const [recipes, setRecipe] = useState(dataRecipes);

    const handleDelete = () => {
        setRecipe(recipes.filter((current) => !current.id))
    }

  return (

    <ul>
      {recipes.map((plate) => {
        return (
             <ListItem recipe={plate} key={plate.id} handle={handleDelete}/>
        )
      })

      }
    </ul>
  )
}

export default List
