import { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate, useParams } from "react-router-dom";

const Update = ({recipes,setRecipes}) => {
    const [name,setNameRecipe] = useState();
    const [calories,setCalories] = useState();
    const [image, setImage] = useState();
    const [servings, setServings] = useState();
    const { recipeId } = useParams();
    const nav = useNavigate();

    const handleUpdateRecipes = (event) => {
        //first always prevent the default refresh of the form
        event.preventDefault();

        //this maps over all the pets and updates only the one whos id matches the params id from above
        const mappedRecipes = recipes.map((recipe) => {
          if (recipe.id == recipeId) {
            const updatedRecipe = { name, calories, image, servings, id:recipe.id };
            return updatedRecipe;
          } else {
            return recipe;
          }
        });
        setRecipes(mappedRecipes);

        //this will navigate after we add the pet
        nav("/");
        // 
    }

  return (
    <>
    <div className="content-page">
       <form className="recipe-form" onSubmit={handleUpdateRecipes} >
      <label>
        Recipe Name:</label>
        <input
          placeholder="name"
          type="text"
          value={name}
          onChange={(event) => {
              setNameRecipe(event.target.value);
          }}
        />

      <label>
        Calories:</label>
        <input
          placeholder="calories"
          type="text"
          value={calories}
          onChange={(event) => {
            setCalories(event.target.value);
          }}
        />

      <label>
        Image:</label>
        <input
          placeholder="url of image"
          type="text"
          value={image}
          onChange={(event) => {
            setImage(event.target.value);
          }}
        />

      <label>
        Servings:</label>
        <input
          placeholder="servings"
          type="text"
          value={servings}
          onChange={(event) => {
            setServings(event.target.value);
          }}
        />

      <button className="create-new" type="submit">Create Recipe</button>
    </form>
    </div>
    </>
  )
}

export default Update
