import ListItem from "./ListItem"
import dataRecipes from "../data/data.json"
import { useState } from "react"
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";

const List = () => {

    const [recipes, setRecipes] = useState(dataRecipes);

  return (

    <ul className="listRecipes">
      {recipes.map((plate) => {
        //   {console.log(plate.id)}
        const { plateId } = useParams();
        const OnePlate = recipes.find((dish) => dish.id == plateId);
        return (
            <Link to={`/recipe/${plate.id}`}>
              <ListItem plate={OnePlate} key={OnePlate.id} setRecipes={setRecipes} allRecipes={recipes}/>

            </Link>
        )
      })

      }
    </ul>
  )
}

export default List
